import { React, useRef, useState } from "react";
import { MovieList, MoviePageLayout, PPAL, PPALLayout, VerticalMovieCard } from "./MovieInfo.style";
import { HorizontalMovieCard, Movie, MovieImg, MovieRating, MovieTitle, StarRating } from "~/components/ReviewForm/ReviewForm.style";
import naruto from 'src/assets/images/naruto.png';
import { Link } from "react-router-dom";
import ReturnBlock from "~/components/ReturnLink/Return";
import { ReviewLayout } from "../Review/Review.style";
import VerticalMvCard from "~/components/VerticalMvCard/VerticalMvCard";
import { Navigator } from "~/components/VerticalMvList/VerticalMvList.style";

const MovieInfo = () => {
    const [rating] = useState(1);
    const movieListRef = useRef(null);

    return (
        <ReviewLayout>
            <Link to='/' style={{ 
                textDecoration:'none', 
                width:'400px', 
                padding:'80px 2.5% 15px' 
            }}>
                <ReturnBlock backmessage="Back to homepage" />
            </Link>
            <MoviePageLayout>
                <HorizontalMovieCard>
                    <MovieImg src={naruto} alt="Movie Picture" />
                    <Movie>
                        <MovieTitle>Movie Title</MovieTitle>
                        <p>
                            Naruto là một loạt manga Nhật Bản được viết và minh họa bởi Kishimoto Masashi. Câu chuyện xoay
                            quanh Uzumaki Naruto, một ninja trẻ muốn tìm cách khẳng định mình để được mọi người công nhận và
                            nuôi ước mơ trở thành Hokage - người lãnh đạo Làng Lá.
                        </p>
                        <MovieRating>
                            <h4 style={{ marginRight:'30px' }}>{rating}</h4>
                            <StarRating size="large" name="rt" value={rating} precision={0.1} readOnly />
                        </MovieRating>
                    </Movie>
                </HorizontalMovieCard>
                <div style={{width: "85%", transform: "translateX(8.8%)"}}>
                    <PPALLayout class="PPAL">
                        <PPAL>
                            <h3>People also like</h3>
                        </PPAL>
                    </PPALLayout>
                    <div>
                        <MovieList ref={movieListRef}>
                            <VerticalMvCard name="Movie No1" />
                            <VerticalMvCard name="Movie No2" />
                            <VerticalMvCard name="Movie No3" />
                            <VerticalMvCard name="Movie No4" />
                            <VerticalMvCard name="Movie No5" />
                            <VerticalMvCard name="Movie No6" />
                            <VerticalMvCard name="Movie No7" />
                            <VerticalMvCard name="Movie No8" />
                        </MovieList>
                        <Navigator
                            onClick={() => {
                                
                            }}
                        />
                    </div>
                </div>
            </MoviePageLayout>
        </ReviewLayout>
    );
};
export default MovieInfo;
