import { Card } from "@mui/material";
import styled from "styled-components";

export const VerticalMovieCard = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    position: relative;
    left: 50%;
    transform: translateX(-50%);
    width: 85%;
    height: 250px;
    border-radius: 30px;
    background-color: #180d0d;
`;

export const PPALLayout = styled.div`
    display: flex;
    flex-direction: column;
    position: relative;
    float: right;
    width: 32%;
    height: 960px;
    border-radius: 30px;
    background-color: #0a674f;
    margin-top: 50px;
    color: #fae6e5;
`;

export const PPAL = styled.div`
    padding: 40px 40px 40px;
`;

export const MoviePageLayout = styled.div`
    display: flex;
    flex-direction: column;
    position: relative;
    width: 100%;
    margin-top: 50px;
    * {
        font-family: var(--boundedFont);
    }
    h1 {
        font-size: var(--primary-text_size);
    }
    h3 {
        font-size: var(--large-text_size);
    }
    p {
        font-size: var(--normal-text_size);
        font-weight: var(--small-fontWeight);
    }
`;

export const MovieList = styled.div`
    display: inline-block;
    flex-direction: column;
    gap: 16px;
    max-width: 800px;
    overflow-y: hidden;
    white-space: nowrap;
`;

export const MovieCard = styled(Card)`
    flex-direction: row;
`;
