import styled from 'styled-components';


const FillInBox = {
    padding: "2% 9% 2% 4%",
    width: "100%",
    height: "100%",
    backgroundColor: "transparent",
    fontSize: "var(--medium-text_size)",
    color: "#fae6e5",
    outline: "none",
    border: "none",
};

export default FillInBox;

export const SignInPage = styled.div`
    text {
        text-align: center;
        color: #fae6e5;
        font-size: var(--medium-text_size);
    } 
`;
export const SignInBox = styled.div`
    width: 50%;
    font-family: arial;
    background-color: #100c0c;
    color: #fae6e5;
    justify-content: space-between;
    text-align: center;
    border-radius: 45px;
    margin-left: auto;
    margin-right: auto;
    margin-top: auto;
    margin-bottom: auto;
    padding: 2% 8% 2% 8%;

    .mainContent {
        text-align: center;
        color: #fae6e5;
        font-style: strong;
        font-size: var(--super-text_size);
        margin-top: 0;
        margin-bottom: 1%;
    }
    form {
        button {
            height: 100%;
            padding: 2.5% 5%;
            margin: 2%;
            background-color: #1d269a;
            color: #fae6e5;
            border-radius: 45px;
            font-size: var(--large-text_size);
            text-align: center;
            cursor: pointer;
            border: none;
        }
    }
    @media (max-width: 480px) {
        width: 84%;
        form {
            button {
                padding: 4% 7%;
                margin: 4%;
                font-size: var(--medium-text_size);
                border: none;
            }
        }
    }
`;
export const InputBox = styled.div`
    width: 100%;
    height: 100%;
    background-color: #231b1b;
    align-content: center;
    color: #fae6e5;
    border: none;
    border-radius: 15px;
    margin-left: auto;
    margin-right: auto;
    margin-bottom: 2.5%;
`;
export const Capybana = styled.text`
    font-family: algerian, fantasy;
    justify-content: center;
    font-size: var(--large-text_size);
`;
