import { React, useState } from 'react';
import {
    CommentArea,
    GeneralReview,
    HorizontalMovieCard,
    Movie,
    MovieImg,
    MovieRating,
    Post,
    PostContainer,
    Review,
    StarPrompt,
    StarRating,
    StarReview,
    StarReviewRating,
    TextReview,
    MovieTitle,
    PPAL
} from './ReviewForm.style';
import naruto from 'src/assets/images/naruto.png';

const CommentSection = () => {
    return (
        <form target="_self" method="post">
            <CommentArea rows="25" placeholder="Whatcha thinking about?" required />
            <PostContainer>
                <Post type="submit" value="Post" />
            </PostContainer>
        </form>
    );
};

const ReviewForm = () => {
    const [rating] = useState(1);
    const [rating2, setRating] = useState(1);

    return (
        <GeneralReview>
            <HorizontalMovieCard>
                <MovieImg src={naruto} alt="Movie Picture" />
                <Movie>
                    <MovieTitle>Movie Title</MovieTitle>
                    <p>
                        Naruto là một loạt manga Nhật Bản được viết và minh họa bởi Kishimoto Masashi. Câu chuyện xoay
                        quanh Uzumaki Naruto, một ninja trẻ muốn tìm cách khẳng định mình để được mọi người công nhận và
                        nuôi ước mơ trở thành Hokage - người lãnh đạo Làng Lá.
                    </p>
                    <MovieRating>
                        <h4 style={{ marginRight:'30px' }}>{rating}</h4>
                        <StarRating size="large" name="rt" value={rating} precision={0.1} readOnly />
                    </MovieRating>
                </Movie>
            </HorizontalMovieCard>
            <div style={{display: "flex", flexDirection: "row", width: "85%", left: "25%", transform: "translateX(-10%)"}}>
                <Review class="review">
                    <StarReview>
                        <h3>How much would you rate this movie?</h3>
                        <StarPrompt>
                            <h3>{rating2}</h3>
                            <StarReviewRating
                                onChange={(event, newValue) => {
                                    setRating(newValue);
                                }}
                                size="medium"
                                name="rt"
                                value={rating2}
                                precision={0.5}
                            />
                        </StarPrompt>
                    </StarReview>
                    <TextReview>
                        <h3>Share your thoughts</h3>
                        <CommentSection />
                    </TextReview>
                </Review>
                <PPAL class="PPAL">
                    <div id = "box">
                        <h3>People also like</h3>
                    </div>
                </PPAL>
            </div>
        </GeneralReview>
    );
};

export default ReviewForm;
