import styled from 'styled-components';
import { TextField } from '@mui/material';

export const NavbarContainer = styled.nav`
    padding: 0px 100px 0px 100px;
    position: sticky;
    top: 0px;
    height: 60px;
    width: 100%;
    background-color: var(--primary-red);
    display: flex;
    align-items: center;
    justify-content: space-between;
    color: white;
`;
export const SearchBlock = styled(TextField)`
    flex-basis: 50%;
    background-color: #d9d9d9;
    border-radius: 45px;
    /* Remove the border for outlined variant */
    .MuiOutlinedInput-root {
        fieldset {
        border: none;
        }

        /* Adjust the input padding and font size to reduce height */
        input {
        padding: 10px 1px; /* Decrease padding to make the height smaller */
        font-size: 30px;    /* Adjust font size if necessary */
        }
    }
`;

export const Logo = styled.div`
    font-size: 1.5em;
    font-weight: bold;
`;

export const MenuItem = styled.div`
    font-size: 1.2em;
    cursor: pointer;
    transition: color 0.3s ease;

    &:hover {
        color: #ff6347;
    }
`;
