import { Movie } from "@mui/icons-material";
import React, { useState } from "react";
import { VerticalMovieCard } from "./MovieInfo.style";
import { GeneralReview, MovieImg, MovieRating, MovieTitle, StarRating } from "~/components/ReviewForm/ReviewForm.style";
import naruto from 'src/assets/images/naruto.png';

const MovieInfo = () => {
    const [rating] = useState(1);
    return (
        <GeneralReview>
            <HorizontalMovieCard>
                <MovieImg src={naruto} alt="Movie Picture" />
                <Movie>
                    <MovieTitle>Movie Title</MovieTitle>
                    <p>
                        Naruto là một loạt manga Nhật Bản được viết và minh họa bởi Kishimoto Masashi. Câu chuyện xoay
                        quanh Uzumaki Naruto, một ninja trẻ muốn tìm cách khẳng định mình để được mọi người công nhận và
                        nuôi ước mơ trở thành Hokage - người lãnh đạo Làng Lá.
                    </p>
                    <MovieRating>
                        <h4 style={{ marginRight:'30px' }}>{rating}</h4>
                        <StarRating size="large" name="rt" value={rating} precision={0.1} readOnly />
                    </MovieRating>
                </Movie>
            </HorizontalMovieCard>
            <div style={{display: "block", width: "85%", transform: "translateX(9%)"}}>
                <Review class="review">
                    <StarReview>
                        <h3>How much would you rate this movie?</h3>
                        <StarPrompt>
                            <h3>{rating2}</h3>
                            <StarReviewRating
                                onChange={(event, newValue) => {
                                    setRating(newValue);
                                }}
                                size="medium"
                                name="rt"
                                value={rating2}
                                precision={0.5}
                            />
                        </StarPrompt>
                    </StarReview>
                    <TextReview>
                        <h3>Share your thoughts</h3>
                        <CommentSection />
                    </TextReview>
                </Review>
                <PPALLayout class="PPAL">
                    <PPAL>
                        <h3>People also like</h3>
                    </PPAL>
                </PPALLayout>
            </div>
        </GeneralReview>
    );
};
export default MovieInfo;
