import styled from "styled-components";

export const VerticalMovieCard = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    position: relative;
    left: 50%;
    transform: translateX(-50%);
    width: 85%;
    height: 250px;
    border-radius: 30px;
    background-color: #180d0d;
`;
