import styled from "styled-components";

export const VerticalMovieCard = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    position: relative;
    left: 50%;
    transform: translateX(-50%);
    width: 85%;
    height: 250px;
    border-radius: 30px;
    background-color: #180d0d;
`;

export const PPALLayout = styled.div`
    display: flex;
    flex-direction: column;
    position: relative;
    float: right;
    width: 32%;
    height: 960px;
    border-radius: 30px;
    background-color: #401313;
    margin-top: 50px;
    color: #fae6e5;
`;

export const PPAL = styled.div`
    padding: 40px 60px 40px;
`;
